<template>
  <s-jumbo
    :size="current.size"
    :into="current.into"
    :small="current.small"
    :title="current.title"
    :class="{'is-home': current.name === 'home'}"
    :background="current.name !== 'home'"
  >
    <s-navbar
      slot="header"
      :items="menu"
      @logo="$router.push({ name: 'home' })"
    />
    <template v-if="current.name === 'home'">
      <template slot="title">Application Storytellingâ„¢<br> that amplifies development</template>
      <s-container
        centered-h
        padding="normal"
      >
        <s-div size="8">
          <s-text
            :padding="['none', '2x']"
            p="1"
            alignment="centered"
          >Storyscript is a top-level programming language and platform that is specially designed for data flow between highly-reusable components (e.g. microservices), written in any other programming language. Bash for the cloud. Best of all, it's open source!</s-text>
        </s-div>
      </s-container>
      <s-container centered-h>
        <s-form-beta />
      </s-container>
    </template>
    <template v-else-if="current.name === 'loading'">
      <template slot="title">
        <i class="mdi mdi-spin mdi-loading" />
      </template>
    </template>
  </s-jumbo>
</template>

<script>
export default {
  name: 'StoryJumbo',
  data: () => ({
    jumbos: [{
      name: 'home',
      size: 'fullheight',
      title: '',
      small: ''
    }, {
      name: 'about',
      size: 'medium',
      title: 'Our Story',
      small: 'About Us'
    }, {
      name: 'contact',
      size: 'medium',
      title: 'Let\'s Talk',
      small: 'Contact'
    }, {
      name: 'events',
      size: 'medium',
      title: 'Upcoming Events',
      small: 'Meet up'
    }],
    visible: false
  }),
  computed: {
    menu: function () {
      return [
        { name: 'Blog', link: '//storyscript.io/blog' },
        { name: 'Docs', link: '//docs.storyscript.io' },
        { name: 'About', route: { name: 'about' }, active: this.$route.name === 'about' },
        { name: 'Hub', link: '//hub.storyscript.io' },
        { name: 'Github', button: { url: '//github.com/storyscript', state: 'secondary', size: 'small', borderless: true, iconRight: 'github-face' } }
      ]
    },
    current: function () {
      return (
        !this.visible
          ? { name: 'loading', size: 'fullheight' }
          : this.jumbos.filter(j => j.name === this.$route.name)[0] ||
            {
              name: 'not-found',
              size: 'fullheight',
              title: '404',
              small: 'Lost in space ?',
              into: 'chevron'
            }
      )
    }
  },
  watch: {
    '$route': function () {
      this.visible = true
    }
  },
  mounted: function () {
    if (this.$route.name) {
      this.visible = true
    }
  }
}
</script>

<style lang="scss">
.jumbo {
  &,
  & > * {
    transition: all 0.3s ease-in-out;
  }
}

.is-home {
  position: relative;

  &:before,
  &:after {
    @include touch {
      display: none;
    }
    z-index: -1;
    content: "";
    display: block;
    position: absolute;
    width: 450px;
    top: 0;
    bottom: 0;
    background-repeat: no-repeat;
  }

  &:before {
    background-image: url("data:image/svg+xml;base64,");
    left: 0;
    background-position: center left;
  }
  &:after {
    background-image: url("data:image/svg+xml;base64,");
    right: 0;
    background-position: center right;
  }
}
</style>
